var types = {
    HANDSHAKE: 'HANDSHAKE',
    PERSON_IN: 'PERSON_IN'
};

function HandshakeMessage(gateID) {
    this.type = types.HANDSHAKE;
    this.gateID = gateID;
}

function PersonPassMessage(personID) {
    this.type = types.PERSON_IN;
    this.personID = personID;
}

exports.types = types;
exports.HandshakeMessage = HandshakeMessage;
exports.PersonPassMessage = PersonPassMessage;